def generate_unique_slugs(apps, schema_editor):
    """Generate unique slugs for all categories"""
    CategoriaTratamiento = apps.get_model('tratamientos', 'CategoriaTratamiento')
    from django.utils.text import slugify
    
    for categoria in CategoriaTratamiento.objects.all():
        if not categoria.slug:
            base_slug = slugify(categoria.nombre) if categoria.nombre else f'categoria-{categoria.pk}'
            slug = base_slug
            counter = 1
            while CategoriaTratamiento.objects.filter(slug=slug).exclude(pk=categoria.pk).exists():
                slug = f"{base_slug}-{counter}"
                counter += 1
            categoria.slug = slug
            categoria.save()

